import React from "react";
import Slider from 'react-slick';

import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

import data from '../dummy/restaurant2.json';

const AutoSlider = () => {
    const settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };
    

    <div className="autoSlider">
        <Slider {...settings}>
            {data.map( item => (
                <div className="menu">
                    <div className="menu-top">
                        <img src={item.rImg} alt={item.restaurant}/>
                        <h1>{item.restaurant}</h1>
                    </div>
                </div>
            
            ))}
        </Slider>
    </div>
}

export default AutoSlider;
