import React from "react";
import Slider from 'react-slick';

import '../css/Slider.css';

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import data from '../dummy/restaurant.json';

const AutoSlider = () => {
    // const settings = {
    //     dots: true,
    //     infinite: false,
    //     speed: 500,
    //     slidesToShow: 4,
    //     slidesToScroll: 4,
    //     initialSlide: 0,
    //     responsive: [
    //       {
    //         breakpoint: 1024,
    //         settings: {
    //           slidesToShow: 3,
    //           slidesToScroll: 3,
    //           infinite: true,
    //           dots: true
    //         }
    //       },
    //       {
    //         breakpoint: 600,
    //         settings: {
    //           slidesToShow: 2,
    //           slidesToScroll: 2,
    //           initialSlide: 2
    //         }
    //       },
    //       {
    //         breakpoint: 480,
    //         settings: {
    //           slidesToShow: 1,
    //           slidesToScroll: 1
    //         }
    //       }
    //     ]
    //   };
    
    
    //auto
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        speed: 2000,
        autoplaySpeed: 2000,
        cssEase: "linear"
      };
    console.log("여기");
    
    return(
        <div className="autoSlider">
        
            <Slider {...settings}>
                {data.list.map( (item, index) => (
                    <div className="menu" key={index}>
                        <div className="menu-top">
                            <img src={process.env.PUBLIC_URL + item.rImg} alt={item.restaurant}/>
                            <h1>{item.restaurant}</h1>
                        </div>
                    </div>
                
                ))}
            </Slider>

        </div>
    );
}

export default AutoSlider;
