import React from 'react';
import {useState, useEffect} from 'react';
import { Link, useLocation } from "react-router-dom";
import userList from "../dummy/user.json"
import restaurant from "../dummy/restaurant.json"


const Introduction = () => {
    const path = useLocation().pathname;
    console.log("주소 : " + path);
    const index = path.split('/');
    console.log("식당 : " + index[2]);

    //const address = restaurant.list.map = (l.restaurant ==  index[2]) => ())

    const address = userList.user.find((r) => {
        if(r.restaurant == index[2])
            return r.address;
        // 오류
    })

    return (
        <div>

            <p style={{ textAlign: "center", margin: 10 }}>소개</p>
            <h3 style={{padding:"10px"}}>스타동 한성대점</h3>
            <div style={{border: "1px dashed" }}>
                <p style={{padding:"10px", fontWeight:"bold"}}>위치</p>
                <p style={{padding:"10px", fontWeight:"bold"}}>{address}</p>
            </div>
        </div>
    );
}

export default Introduction;
