import React, {useState, useEffect} from "react";
import styeld from 'styled-components';

const SubwaySlider = () => {

    const slideRef = useRef();
    const [currentImgX, setCurrentImgX] = useState(0);
    
    const Img_Width = 200;
    const slideRange = currentImgX * Img_Width; //슬라이드 범위

    //현재 이미지의 X 위치가 바뀌면
    useEffect(() => {
        slideRef.current.style.transition = `translateX(${slideRange}pw)`;        
    }, [currentImgX]);

    const nextSlide = () => {
        if(currentImgX === 2) return;
        setCurrentImgX(currentImgX +1);
    }
    const prevSlide = () => {
        if(currentImgX === 0) return;
        setCurrentImgX(currentImgX -1);
    }

    return (
        <>
            <Wrapper>
                <SlideWrapper ref={slideRef}>
                <Img src="../img/haehwaSubway.png" />
                <Img src="../img/hansungSubway.png" />
                <Img src="../img/sungsinSubway.png" />
                </SlideWrapper>
             </Wrapper>
            
            <div>
                <button >Left</button>
                <button >Right</button>
            </div>
        </>
    );
}

export default SubwaySlider;

const Wrapper = styeld.div`
  width: 200px;
  height: 200px;
  overflow: hidden;
`;

const SlideWrapper = styeld.div`
  display: flex;
  width: 100%;
  height:100%;
`;

const Img = styeld.img`
  width: 200px;
  height: 200px;
`;
