import '../css/Carousel.css';
import { useEffect, useRef, useState } from 'react';

const Carousel = () => {

    const container_Carousel = useRef();
    const [nowX, setNowX] = useState(0);

    useEffect(() => {
        container_Carousel.current.style.transform = `translateX(${nowX}vw)`;
    }, [nowX]);

    const clickLeftButton = () => {
        setNowX((prop) => prop + 20);
    };
    const clickRightButton = () => {
        setNowX(nowX - 20);
        console.log(`its's work ${nowX}`);
    };

    return (
        <div className='body'>
            <div>{nowX}</div>
            <label className='left' onClick={clickLeftButton}>left</label>
            <label className='right' onClick={clickRightButton}>right</label>

            <div className='container_Carousel' ref={container_Carousel}>
                <div className='inner'>
                    <img src={"../img/rImg/돈시락.jpeg"} alt=""/>
                </div>
                <div className='inner'>
                    <img src={"../img/rImg/라라면가.jpeg"} alt=""/>
                </div>
                <div className='inner'>
                    <img src={"../img/rImg/부부김밥.png"} alt=""/>
                </div>
                <div className='inner'>
                    <img src={"../img/rImg/송림원.png"} alt=""/>
                </div>

            </div>
            
        </div>
    );
}

export default Carousel;
