import React from 'react';
import {useState, useEffect} from 'react';
import { useNavigate, Link} from "react-router-dom";
import userList from "../dummy/user.json"

//import {createUserWithEmailAndPassword} from 'firebase/auth';
//import {onAuthStateChanged} from 'firebase/auth';
//import {signInWithEmailAndPassword} from 'firebase/auth';
//import {signOut} from 'firebase/auth';
//import { getAuth } from "firebase/auth";

const Login = () => {

  // 회원가입 
  //const [registerEmail, setRegisterEmail] = useState("");
  //const [registerPassword, setRegisterPassword] = useState("");

  // 로그인 
  const [loginId, setLoginId] = useState(""); 
  const [loginPassword, setLoginPassword] = useState("");
  const [check, setCheck] = useState(false); 


  useEffect(() => {
    if (loginId || loginPassword) return;
    setLoginId(loginId);
    setLoginPassword(loginPassword);
  }, [loginId, loginPassword]);

  const onChange = (event) => {
    const {target: {name, value}} = event;
    if (name==="loginEmail") {
      setLoginId(value)
    } else if (name=== "loginPassword") {
      setLoginPassword(value);
    }
  }

  /*  
  //회원가입
  const register = async () => {
    try {
      const user = await createUserWithEmailAndPassword(
        getAuth,
        registerEmail,
        registerPassword
      );
      console.log(user);
    } catch (error) {
      console.log(error.message);
    }
  };
  */
  //로그인
  const login = () => {
    const idCheck = false;
    const pwCheck = false;
    try {
      userList.user.map(id => (id === loginId) ? idCheck=true : idCheck =false );
      userList.user.map(pw => (pw === loginPassword) ? pwCheck=true : pwCheck =false );
      (idCheck === true && pwCheck === true) ? setCheck(true) : setCheck(false);
      console.log(check);
    } catch (error) {
        console.log(error.message);
    }
  };

  //로그아웃
  const logout = () => {
    //signOut(getAuth);
  };


  return (
    <div style={{ textAlign: "center", margin: 10 }}>

      <div>
        <h3>Login</h3>
        <form>
        <input
          type="text"
          name="loginEmail"
          value={loginId}
          placeholder="Email"
          onChange={(e)=>{
            setLoginId(e.target.value);
          }}
        />
        <input
          type="password"
          name="loginPassword"
          value={loginPassword}
          placeholder="Password"
          onChange={(e)=>{
            setLoginPassword(e.target.value);
          }}
        />
        <Link to={"/mypage"}>
          <button >Login</button>
        </Link>

        </form>
      </div>
    </div>
  );
}

export default Login;
