            setCurrentList(slideLength);
        }
        else {
            setCurrentList(currentList-1);
        }
    }
    const next= () => {
        if(currentList >= slideLength) {
            setCurrentList(0);
        }
        else {
            setCurrentList(currentList+1);
        }
    }

    const [currentList, setCurrentList] = useState(1)

