import React from 'react';
import { FaChevronLeft } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import '../css/SideBar.css'

const SideBar = ({sidebar, closeSidebar}) => {
    return (
        <div id="sidemenu" className={sidebar?"sidebar sidebar--open":"sidebar"}>
            <li onClick={closeSidebar}><FaChevronLeft /></li>
            <li><Link to="/korean">한식</Link></li>
            <li><Link to="/chinese" style={{ color: '#ffffff' }}>중식</Link></li>
            <li><Link to="/japanese" style={{ color: '#ffffff' }}>일식</Link></li>
            <li><Link to="/western" style={{ color: '#ffffff' }}>양식</Link></li>
            <li>카페 / 디저트</li>
        </div>
    )
}

export default SideBar;
