import React from 'react';
import Backdrop from './Backdrop';
import CategoryBar from './CategoryBar';
import siteLogo from './Logo1.JPG';

const HeaderMenu = () => {
    const [ CategoryBar, setCategoryBar ] = useState(false)

    const toggleCategoryBar = () => {
        setCategoryBar(prevState => !prevState)
    }

    return (
        <div className='HeaderBar'>
            <ul>
                <li>
                    <CategoryBar openCategoryBar={toggleCategoryBar}/>
                    <Backdrop />
                </li>
                <li className='siteTitle'>
                    <a href='/'>
                        <img src={siteLogo} alt='Taste of Hansung' className='Logo' />
                    </a>
                </li>
                <li className='station'>성신여대입구</li>
                <li className='station'>한성대입구</li>
                <li className='station'>혜화</li>
            </ul>
        </div>
    )
}

export default HeaderMenu;
